mapper.select:
select dep.*,usr.username as principalName FROM gw_depot dep left join gw_user usr on usr.id=dep.principal and ifnull(usr.status,'0') not in('1','2') where 1=1
and dep.name like '%${name}%'
and dep.type=${type}
and dep.remark like '%${remark}%'
and ifnull(dep.delete_Flag,'0') !='1' order by dep.sort asc
limit #{offset},#{rows}

mapper.update:
update gw_depot
set delete_Flag='1'
where 1=1
and id in (

    #{id}

)
