mapper.select:
select m.*,u.name unit_name,mc.name categoryName,me.bar_code m_bar_code,me.id meId,me.commodity_unit from gw_material m left join gw_material_extend me on m.id=me.material_id and ifnull(me.delete_Flag,'0') !='1' left join gw_unit u on m.unit_id=u.id and ifnull(u.delete_Flag,'0') !='1' left JOIN gw_material_category mc on m.category_id = mc.id and ifnull(mc.status,'0') !='2' where m.enabled=1 and me.id is not null
and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})
and m.category_id in
#{item}
and ifnull(m.delete_flag,'0') !='1' ORDER BY id desc
limit #{offset},#{rows}

select m.* FROM gw_material m where 1=1
and m.name =#{name}
and ifnull(m.delete_flag,'0') !='1'

select id, category_id, name, mfrs, safety_stock, model, standard, color, unit, remark, unit_id, enabled,enable_serial_number FROM gw_material where 1=1 and enabled ='1' and enable_serial_number ='1'
and name like #{name}
and model like #{model}
and ifnull(delete_flag,'0') !='1' order by id desc

select count(1) FROM gw_material where 1=1 and enabled ='1' and enable_serial_number ='1'
and name like #{name}
and model like #{model}
and ifnull(delete_flag,'0') !='1'

